package me.xiaozhangup.musicstom.event.display;

import net.kyori.adventure.text.Component;
import net.minestom.server.MinecraftServer;
import net.minestom.server.event.GlobalEventHandler;
import net.minestom.server.event.server.ServerListPingEvent;
import net.minestom.server.ping.ResponseData;

public class MOTD {

    public static void with(GlobalEventHandler globalEventHandler) {
        globalEventHandler.addListener(ServerListPingEvent.class, event -> {
            ResponseData responseData = event.getResponseData();
            responseData.setMaxPlayer(50000);
            responseData.setOnline(MinecraftServer.getConnectionManager().getOnlinePlayers().size());
            responseData.setFavicon("data:image/png;base64,");
            responseData.setDescription(Component.text("MusicStom"));
            event.setResponseData(responseData);
        });
    }

}
